$folder_name = Read-Host "Enter the folder name you want to create"; $folder_name = $folder_name -replace '[<>:"/\\|?*]', '_'; $base_path = Get-Location; $full_path = Join-Path $base_path $folder_name; Write-Host "Creating folder '$folder_name' at '$base_path'..."; New-Item -ItemType Directory -Path $full_path -Force | Out-Null; New-Item -ItemType File -Path (Join-Path $full_path "1.go") -Force | Out-Null; New-Item -ItemType File -Path (Join-Path $full_path "2.go") -Force | Out-Null; New-Item -ItemType File -Path (Join-Path $full_path "3.go") -Force | Out-Null; New-Item -ItemType File -Path (Join-Path $full_path "all.json") -Force | Out-Null; Write-Host "âœ… Folder '$folder_name' created with files: 1.go, 2.go, 3.go, all.json"
