package main
import "fmt"

type Person struct {name string}

func (p Person) changeName(newName string) {
    p.name = newName // copy updated
}

func main() {
    a := Person{"Alice"}
    a.changeName("Bob")
    fmt.Println(a.name) // still Alice
}
```

```go
func (p *Person) changeName(newName string) {
    p.name = newName // updates original
}

func main() {
    a := Person{"Alice"}
    a.changeName("Bob")
    fmt.Println(a.name) // Bob
}
```
